properties.default:
"css`
    h2 {
      color: #000;
      text-align: center;
      font-size: 2em;
    }
    .wrapper {
      display: flex;
      flex-direction: column;
      align-items: center;
    }
    .tab {
      cursor: pointer;
      padding: 10px 20px;
      margin: 0px 2px;
      background: #000;
      display: inline-block;
      color: #fff;
      border-radius: 3px 3px 0px 0px;
      box-shadow: 0 0.5rem 0.8rem #00000080;
    }
    .panels {
      background: #fffffff6;
      box-shadow: 0 2rem 2rem #00000080;
      min-height: 200px;
      max-width: 945px;
      border-radius: 3px;
      overflow: hidden;
      padding: 20px;
    }
    .panel {
      display: none;
      animation: fadein 0.8s;
    }
    @keyframes fadein {
      from {
        opacity: 0;
      }
      to {
        opacity: 1;
      }
    }
    .panel-title {
      font-size: 1.5em;
      font-weight: bold;
      text-align: center;
    }
    .radio {
      display: none;
    }
    #one:checked ~ .panels #one-panel,
    #two:checked ~ .panels #two-panel,
    #three:checked ~ .panels #three-panel,
    #four:checked ~ .panels #four-panel {
      display: block;
    }
    #one:checked ~ .tabs #one-tab,
    #two:checked ~ .tabs #two-tab,
    #three:checked ~ .tabs #three-tab,
    #four:checked ~ .tabs #four-tab {
      background: #fffffff6;
      color: #000;
      border-top: 3px solid #000;
    }

    .card {
      box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
      border: black;
      border-radius: 0.5em;
      border-style: solid;
      transition: 0.3s;
      flex: 1 0 25%;
      margin: 5px 15px 5px 15px;
      color: dodgerblue;
    }

    .card:hover {
      box-shadow: 0 8px 16px 0 rgba(0, 0, 0, 0.4);
    }

    img {
      border-radius: 5px 5px 0 0;
    }

    .items {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;
      text-align: center;
      justify-content: space-between;
      padding-top: 10px;
      margin-left: 80px;
      margin-right: 80px;
    }

    .icon {
      width: 100px;
      padding-top: 10px;
    }

    .container {
      flex: 1 0 25%;
      padding: 5px;
    }

    .text {
      margin: 15px 15px 5px 15px;
      text-align: justify;
    }

    .button {
      background-color: transparent;
      border: none;
      color: dodgerblue;
      font-weight: bold;
      width: 100%;
      height: 50px;
      text-align: center;
      text-decoration: none;
      display: inline-block;
      font-size: 16px;
    }

    .button:hover {
      border-top: 3px solid #000;
    }
  `"
